import { useEffect, useRef } from "react";
// Validation
import { validateIndustry } from "../../utils/validation/validateIndustry";
import { incrementForm } from "../../utils/validation/incrementForm";
// Material UI
import { makeStyles } from "@material-ui/core/styles";
// Components
import Industry from "./Industry";
import Navigation from "./Navigation";

function BusinessIndustry({
  values,
  handleChange,
  handleDirectChange,
  step,
  setStep,
  handleErrors,
  errors,
}) {
  const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
      minHeight: "80vh",

      overflow: "scroll",
    },
    textField: {
      width: "17rem",
      marginTop: "2rem",
      flexGrow: 1,
      flexShrink: 1,
    },
    innerWrapper: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "flex-start",
      alignItems: "center",
      minHeight: "20rem",
    },
    header: {
      textAlign: "center",
      margin: 0,
      marginBottom: 20,
      fontWeight: 500,
      fontFamily: "Open Sans, sans-serif",
    },
    text: {
      textAlign: "center",
      width: "15rem",
      margin: 40,
      fontWeight: 500,
      fontSize: 16,
      color: values.industry === "" ? "black" : "green",
      fontFamily: "Open Sans, sans-serif",
    },

    button: {
      marginTop: "3rem",
      marginBottom: "3rem",
    },
    buttonContainer: {
      display: "flex",
      marginBottom: 25,
      width: 200,
      justifyContent: "space-between",
    },
  }));

  useEffect(() => {}, [errors]);

  const Ref = useRef(null);

  const classes = useStyles();

  return (
    <form
      className={classes.root}
      onSubmit={(e) => {
        e.preventDefault();
        incrementForm(e, values, handleErrors, validateIndustry, setStep);
      }}
    >
      <div className={classes.innerWrapper}>
        <h1 className={classes.header}>Industry</h1>
        <p className={classes.text}>
          {values.industry === ""
            ? "What is your business's industry?"
            : values.industry}
        </p>

        <Industry
          handleChange={handleDirectChange}
          errors={errors}
          values={values}
        />

        <Navigation
          errors={errors}
          values={values}
          setStep={setStep}
          handleErrors={handleErrors}
          validation={validateIndustry}
        />
      </div>
    </form>
  );
}

export default BusinessIndustry;
