import Container from "@material-ui/core/Container";
import { makeStyles } from "@material-ui/core/styles";
import { Button, ButtonGroup } from "@material-ui/core";
import PublicIcon from "@material-ui/icons/Public";
import RoomIcon from "@material-ui/icons/Room";
import StoreIcon from "@material-ui/icons/Store";
import Chip from "@material-ui/core/Chip";
import Follow from "./Follow";
import Followers from "./Followers";
import Following from "./Following";

import PostCard from "../Post/Main/Card";

import { useRef, useEffect, useState } from "react";
import { fetchUserPosts } from "./utils/fetchUserPosts";

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start",
    width: "100%",
    height: "100%",

    marginTop: 85,
  },

  body: {
    display: "flex",
    flexDirection: "column",
    width: "100%",
    height: "100%",
  },

  bodyPost: {
    display: "flex",
    flexDirection: "column",
    width: "100%",
  },

  overview: {
    width: 300,
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start",

    margin: 0,
    padding: 0,
  },

  title: {
    textDecoration: "none",
    color: "grey",
    fontFamily: "Open Sans, sans-serif",
    fontWeight: 500,
    fontSize: 18,
    textAlign: "left",
    width: "100%",
    margin: 0,
    marginBottom: 25,
    marginTop: 30,
  },

  storyTitle: {
    textDecoration: "none",
    color: "black",
    fontFamily: "Noto Sans, sans-serif",
    fontWeight: 700,
    fontSize: 22,
    textAlign: "left",
    width: "100%",
    margin: 0,
    marginBottom: 55,
    marginTop: 5,
  },

  imageContainer: {
    display: "flex",
    width: 150,
    height: 150,
    alignItems: "center",
  },
  image: {
    borderRadius: "50%",
    width: "100%",
    height: "100%",
    objectFit: "cover",
  },
  articleImage: {
    width: "85%",
    padding: 10,
    marginTop: 30,
    marginBottom: 30,
    objectFit: "cover",
  },
  article: {
    right: 0,
    display: "flex",
    flexDirection: "column",
    width: 300,
    height: "100%",
    margin: 0,
  },
  innerWrapper: {
    display: "flex",
    flexDirection: "column",
    width: "100%",
    height: "100%",
    margin: 0,
    marginTop: 30,
  },
  text: {
    textDecoration: "none",
    color: "black",
    fontFamily: "Open Sans, sans-serif",
    fontWeight: 300,
    fontSize: 16,
    width: "95%",
    lineHeight: "32px",
    marginBottom: 40,
  },
  author: {
    color: "black",
    fontFamily: "Noto Sans, sans-serif",
    fontWeight: 700,
    fontSize: 14,
    width: "80%",
    margin: 0,
  },
  subheader: {
    color: "black",
    fontFamily: "Noto Sans, sans-serif",
    fontWeight: 700,
    fontSize: 16,
    width: "80%",
    margin: 0,
  },
  date: {
    color: "black",
    fontFamily: "Open Sans, sans-serif",
    fontWeight: 500,
    fontSize: 13,
    width: "100%",
    marginTop: 10,
    lineHeight: 1.5,
  },

  link: {
    color: "black",
    fontFamily: "Frank Ruhl Libre, serif",
    fontWeight: 500,
    fontSize: "18px",
    width: "80%",
    marginTop: 10,
  },
  button: {
    marginTop: 25,
    marginBottom: 20,
    width: 200,
    textTransform: "none",
  },
  chip: {
    marginRight: theme.spacing(1),
    marginBottom: 10,
    paddingLeft: 5,
    paddingRight: 5,
    fontFamily: "Open Sans, sans-serif",
    fontWeight: 700,
    fontSize: 9,
    backgroundColor: "#E5E5E5",
    "&:hover": {
      opacity: 0.5,
      cursor: "pointer",
    },
  },
  chipArray: {
    display: "flex",
    justifyContent: "flex-start",
    flexWrap: "wrap",
    width: "100%",
    marginBottom: 25,
    marginTop: 15,
  },
  followBox: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
  },
  followBoxWrapper: {
    display: "flex",
    marginTop: 20,
    width: 300,
    justifyContent: "space-between",
  },
  followBoxHeader: {
    fontFamily: "Noto Sans, sans-serif",
    fontWeight: 400,
    fontSize: 11,
    color: "grey",
    textAlign: "center",
    margin: 0,
  },
  followBoxText: {
    fontFamily: "Noto Sans, sans-serif",
    fontSize: 14,
    fontWeight: 700,
    color: "black",
    textAlign: "center",
    margin: 0,
  },
  buttonGroup: {
    width: "100%",
    display: "flex",
    justifyContent: "center",
  },
}));

function Article({ story }) {
  const overviewRef = useRef(null);
  const classes = useStyles();
